import type { Ticker } from 'pixi.js/lib/ticker/Ticker';
import type { ResizePluginOptions } from 'pixi.js/lib/app/ResizePlugin';
import type { TickerPluginOptions } from 'pixi.js/lib/app/TickerPlugin';

declare global
{
    namespace PixiMixins
    {
        // Extend the Application interface with resize and ticker functionalities
        interface Application
        {
            resizeTo: Window | HTMLElement;
            resize(): void;
            queueResize(): void;
            cancelResize(): void;

            ticker: Ticker;
            stop(): void;
            start(): void;
        }

        // Combine ResizePluginOptions and TickerPluginOptions into ApplicationOptions
        interface ApplicationOptions extends ResizePluginOptions, TickerPluginOptions {}
    }
}

export {};
